# Find the longest common subsequence of two strings s1 and s2
# using dynamic programming

def longest_common_subsequence(s1, s2):

 # Get the length of both strings
 len1 = len(s1)
 len2 = len(s2)

 # Create a dictionary to store the dynamic programming results
 dp = {}

 # Get the length of the longest common subsequence so far
 for i in range(len1):
 for j in range(len2):
 if s1[i] == s2[j]:
 if i == 0 or j == 0:
 continue
 else:
 dp = max(dp, (i, j))

 # Return the longest common subsequence
 return dp[1][1]

