  \pgfmathtruncatemacro{\rownum}{int(#1)}%
  \xdef\temp{%
    \noexpand\pgforlist\noexpand\rsquare\noexpand\j QuinnCookFEN,{%
      1,...,8%
    }%
    \edef\rsquare{\square{\rownum*\numexpr8+\the\numexpr j\relax}}%
